PREFIX xsd:    <http://www.w3.org/2001/XMLSchema#>
PREFIX schema: <http://schema.org/>
PREFIX rdf:    <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs:   <http://www.w3.org/2000/01/rdf-schema#>
PREFIX iisgv:  <https://iisg.amsterdam/openarchdeaths1910_1920/vocab/>
PREFIX iisgv2: <https://iisg.amsterdam/openarchdeath/vocab/>
PREFIX gg:     <http://www.gemeentegeschiedenis.nl/gg-schema#>
PREFIX geo:    <http://www.opengis.net/ont/geosparql#>
PREFIX bio:    <http://purl.org/vocab/bio/0.1/>

SELECT ?month (sum(if(?year = "1910"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1910) 
              (sum(if(?year = "1913"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1913)
              (sum(if(?year = "1918"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1918)
              (sum(if(?year = "1919"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1919)

 WHERE { ?death a bio:Death .
         ?death iisgv:deceased ?deceased .
         ?death bio:date ?date .
         BIND((month(?date)) as ?month)
         BIND(year(?date) as ?year) 
       }

 ORDER BY ASC (?month)

gchart { "chartConfig": { "options": { "hAxis": { "useFormatFromData": true, "viewWindow": { "max": null, "min": null }, "minValue": null, "maxValue": null, "title": "Age" }, "legacyScatterChartLabels": true, "tooltip": {"isHtml": true}, "vAxes": [ { "useFormatFromData": true, "viewWindow": { "max": null, "min": null }, "minValue": null, "maxValue": null, "title": "%" }, { "useFormatFromData": true, "viewWindow": { "max": null, "min": null }, "minValue": null, "maxValue": null } ], "curveType": "", "booleanRole": "certainty", "lineWidth": 2, "legend": "right", "width": 600, "height": 371, "useFirstColumnAsDomain": true }, "state": {}, "view": { "columns": null, "rows": null }, "isDefaultVisualization": false, "chartType": "LineChart" } }
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> PREFIX schema: <http://schema.org/> PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX iisgv: <https://iisg.amsterdam/openarchdeaths1910_1920/vocab/> PREFIX iisgv2: <https://iisg.amsterdam/openarchdeath/vocab/> PREFIX gg: <http://www.gemeentegeschiedenis.nl/gg-schema#> PREFIX geo: <http://www.opengis.net/ont/geosparql#> PREFIX bio: <http://purl.org/vocab/bio/0.1/>
SELECT ?age (?1910/xsd:decimal(?total_1910)*100 as?share_1910) (?november_1918/xsd:decimal(?november_1918_total)*100 as ?share_november_1918) (?winter_1919/xsd:decimal(?winter_1919_total)*100 as ?share_winter_1919)
WHERE { { select ?total_1910 ?age ?november_1918_total ?winter_1919_total (sum(if(?year = "1918"^^<http://www.w3.org/2001/XMLSchema#int> && ?month = 11, 1, 0)) as ?november_1918) (sum(if(?year = "1910"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1910) (sum(if(?year = "1919"^^<http://www.w3.org/2001/XMLSchema#int> && ?month >= 3, 1, 0)) as ?winter_1919) { ?death a bio:Death . ?death iisgv:deceased ?deceased . # ?deceased schema:gender "m" . ?deceased iisgv:ageYears ?age. ?death bio:date ?date . BIND(year(?date) as ?year) BIND(month(?date) as ?month) FILTER(?age > 0) # excluded because there may still be an issue with age=0 { select (sum(if(?year = "1910"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?total_1910) (sum(if(?year = "1918"^^<http://www.w3.org/2001/XMLSchema#int> && ?month = 11, 1, 0)) as ?november_1918_total) (sum(if(?year = "1919"^^<http://www.w3.org/2001/XMLSchema#int> && ?month >= 3, 1, 0)) as ?winter_1919_total) { ?death a bio:Death . ?death iisgv:deceased ?deceased . #?deceased schema:gender "m" . ?deceased iisgv:ageYears ?age. ?death bio:date ?date . BIND(year(?date) as ?year) BIND(month(?date) as ?month) FILTER(?age > 0) # excluded because there may still be a slight issue with age=0 } }
} }}
ORDER BY ASC (?age)

Correlation between mean excess mortality (ages 18-45) and social class :
gchart { "chartConfig": { "options": { "hAxis": { "useFormatFromData": true, "viewWindow": { "max": null, "min": 40 }, "minValue": 40, "maxValue": null, "title": "Hiscam", "viewWindowMode": "explicit" }, "legacyScatterChartLabels": true, "tooltip": { "isHtml": true, "ignoreBounds": false }, "vAxes": [ { "useFormatFromData": true, "viewWindow": { "max": null, "min": null }, "minValue": null, "maxValue": null, "title": "excess death rate" }, { "useFormatFromData": true, "viewWindow": { "max": null, "min": null }, "minValue": null, "maxValue": null } ], "lineWidth": 0, "booleanRole": "certainty", "pointSize": 7, "legend": "right", "width": 600, "height": 371, "trendlines": { "0": { "type": "linear", "pointSize": 0, "visibleInLegend": true, "lineWidth": 2, "opacity": 0.4, "labelInLegend": "Trendline for Data series 1", "showR2": true, "degree": 2 } } }, "state": {}, "view": { "columns": null, "rows": null }, "isDefaultVisualization": false, "chartType": "ScatterChart" } }
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#> PREFIX schema: <http://schema.org/> PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX iisgv: <https://iisg.amsterdam/openarchdeaths1910_1920/vocab/> PREFIX iisgv2: <https://iisg.amsterdam/openarchdeath/vocab/> PREFIX gg: <http://www.gemeentegeschiedenis.nl/gg-schema#> PREFIX geo: <http://www.opengis.net/ont/geosparql#> PREFIX bio: <http://purl.org/vocab/bio/0.1/> PREFIX bif: <http://www.openlinksw.com/schemas/bif#>
SELECT ?hiscam ?excess
WHERE {
FILTER(?mean >2) # to avoid 'division by 0' error FILTER(?1918 >0) { select ?hiscam (sum(if(?year = "1918"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1918) (sum(if(?year = "1910"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1910) (sum(if(?year = "1911"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1911) (sum(if(?year = "1912"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1912) (sum(if(?year = "1913"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1913) (sum(if(?year = "1914"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1914) (sum(if(?year = "1915"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1915) (sum(if(?year = "1916"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1916) (sum(if(?year = "1917"^^<http://www.w3.org/2001/XMLSchema#int>, 1, 0)) as ?1917) { ?death a bio:Death . ?death iisgv:deceased ?deceased . ?deceased iisgv:ageYears ?age . ?deceased schema:hasOccupation ?occ . ?occ iisgv2:hiscam_nl ?hiscam . ?death bio:date ?date .
BIND(year(?date) as ?year) FILTER(?age >= 18 &amp;&amp; ?age <= 45) FILTER(?hiscam >39) } } BIND(xsd:float(?1910+?1911+?1912+?1913+?1914+?1915+?1916+?1917)/8 as ?mean) BIND(xsd:decimal(?1918/?mean) as ?excess) }
ORDER BY DESC (?excess)
